void init(void)
{
	glPixelStorei(GL_UNPACK_ALIGNMENT, 1);
	glClearColor(0.0, 0.0, 0.0, 0.0);
}
void display(void)
{
	glClear(GL_COLOR_BUFFER_BIT);
	glColor3f(1.0, 1.0, 1.0);
	//thiết lập lại vị trí hiện tại
	glRasterPos2i(20, 20);
	//lệnh vẽ
	glBitmap(40, 40, 0.0, 0.0, 40.0, 0.0, vRasters);
	glBitmap(40, 40, 0.0, 0.0, 12.0, -6.0, tRasters);
	glBitmap(40, 40, 0.0, 0.0, 22.0, 0.0, iRasters);
	glBitmap(40, 40, 0.0, 0.0, 12.0, -4.0, eRasters);
	glBitmap(40, 40, 0.0, 0.0, 22.0, 0.0, nRasters);
	glFlush();
}
void reshape(int w, int h)
{
	glViewport(0, 0, (GLsizei)w, (GLsizei)h);
	glMatrixMode(GL_PROJECTION);
	glLoadIdentity();
	glOrtho(0, w, 0, h, -1.0, 1.0);
	glMatrixMode(GL_MODELVIEW);
}
void keyboard(unsigned char key, int x, int y)
{
	switch (key) {
	case 27:
			exit(0);
	}
}
int main(int argc, char** argv)
{
	glutInit(&argc, argv);
	glutInitDisplayMode(GLUT_SINGLE | GLUT_RGB);
	glutInitWindowSize(200, 100);
	glutInitWindowPosition(100, 100);
	glutCreateWindow(argv[0]);
	init();
	glutReshapeFunc(reshape);
	glutKeyboardFunc(keyboard);
	glutDisplayFunc(display);
	glutMainLoop();
	return 0;
}
